import { createElement as h } from 'react';
import { classBuilder } from '@not-govuk/component-helpers';
import '../assets/Pagination.scss';
export const Pagination = ({ children, classBlock, classModifiers, className, results, resultsPerPage, page, ...attrs }) => {
    const classes = classBuilder('hods-pagination', classBlock, classModifiers, className);
    page = Number(page); // in case user passes in a string
    results = Number(results);
    resultsPerPage = Number(resultsPerPage);
    const resultsFrom = (page - 1) * resultsPerPage + 1;
    const maxResults = page * resultsPerPage;
    const maxPages = results / resultsPerPage + 1;
    const resultsTo = (results < maxResults
        ? results
        : maxResults);
    const pages = [page - 2, page - 1, page, page + 1, page + 2];
    const it = [0, 1, 2, 3, 4];
    const hl = ["?page=" + pages[0], "?page=" + pages[1], "",
        "?page=" + pages[3], "?page=" + pages[4]];
    return (h("div", { className: classes() },
        h("div", { className: classes('summary') },
            "Showing ",
            resultsFrom,
            " - ",
            resultsTo,
            " of ",
            results,
            " results"),
        h("ul", { className: classes('list-items') },
            page > 1 ?
                h("li", { className: classes('item'), id: "prevButton", key: "prev" },
                    h("a", { className: classes('link'), href: page > 1 ? hl[1] : "" },
                        h("span", { "aria-hidden": "true", role: "presentation" }, "\u00AB"),
                        " Previous"))
                : null,
            it.map(i => (h("li", { className: classes('item'), key: i }, pages[i] < 1 || pages[i] > maxPages ? null : h("a", { className: classes('link', pages[i] === page ? 'current' : undefined), href: hl[i] }, pages[i])))),
            resultsTo == results ? null :
                h("li", { className: classes('item'), id: "nextButton", key: "next" },
                    h("a", { className: classes('link'), href: resultsTo == results ? "" : hl[3] },
                        "Next ",
                        h("span", { "aria-hidden": "true", role: "presentation" }, "\u00BB"))))));
};
export default Pagination;
